import { Component, OnInit } from '@angular/core';
import { RouterOutlet, Router, RouterLink, RouterLinkActive } from '@angular/router';
import { CommonModule } from '@angular/common';
import { AuthService } from './auth.service';

@Component({
  selector: 'app-root',
  imports: [RouterOutlet, RouterLink, RouterLinkActive, CommonModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss'
})
export class AppComponent implements OnInit {
  title = 'Customer Lead Management';
  isAuthenticated = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    // Check if user is authenticated on page refresh
    const isAuth = this.authService.isAuthenticated();
    this.isAuthenticated = isAuth;
    console.log('Initial auth status:', this.isAuthenticated);
    
    // Listen for authentication changes
    this.authService.authStatus$.subscribe((status: boolean) => {
      this.isAuthenticated = status;
      console.log('Auth status changed:', this.isAuthenticated);
    });
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<!-- Corporate Header -->
<header class="corporate-header">
  <div class="header-container">
    <div class="brand">
      <div class="brand-logo">CL MS</div>
      <div class="brand-text">
        <h1>Customer Lead</h1>
        <span>Management System</span>
      </div>
    </div>
    <nav class="main-nav" *ngIf="isAuthenticated">
      <a routerLink="/customers" routerLinkActive="active">Dashboard</a>
      <a routerLink="/pending-customers" routerLinkActive="active">Pending</a>
      <button class="logout-btn" (click)="logout()">Logout</button>
    </nav>
  </div>
</header>

<!-- Main Content -->
<main class="main-content">
  <router-outlet></router-outlet>
</main>

<!-- Survey Widget -->
<script 
  src="https://atuldhuri.github.io/Survey_Widget/survey-popup-loader.js"
  data-title="Customer Feedback"
  data-position="bottom-right"
  data-delay="5000">
</script>
