import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { EnquiryFormService, CustomerEnquiry } from './enquiry-form.service';
import { catchError, finalize } from 'rxjs/operators';
import { of } from 'rxjs';

@Component({
  selector: 'app-enquiry-form',
  imports: [ReactiveFormsModule, CommonModule],
  templateUrl: './enquiry-form.component.html',
  styleUrl: './enquiry-form.component.scss'
})
export class EnquiryFormComponent implements OnInit {
  enquiryForm!: FormGroup;
  submitted = false;
  isLoading = false;

  // Income source options
  incomeSources = [
    { value: 'salary', label: 'Salary' },
    { value: 'business', label: 'Business' },
    { value: 'freelance', label: 'Freelance' },
    { value: 'pension', label: 'Pension' },
    { value: 'investment', label: 'Investment' },
    { value: 'other', label: 'Other' }
  ];

  // Reference options
  referenceOptions = [
    { value: 'social_media', label: 'Social Media' },
    { value: 'friend', label: 'Friend' },
    { value: 'advertisement', label: 'Advertisement' },
    { value: 'website', label: 'Website' },
    { value: 'agent', label: 'Agent' },
    { value: 'other', label: 'Other' }
  ];

  // Property interest options
  propertyInterests = [
    { value: 'studio-apt', label: 'Studio Apt', checked: false },
    { value: '1-bhk', label: '1 BHK', checked: false },
    { value: '2-bhk', label: '2 BHK', checked: false },
    { value: '3-bhk', label: '3 BHK', checked: false },
    { value: 'jodi-flat', label: 'JodiFlat', checked: false }
  ];

  constructor(
    private fb: FormBuilder,
    private enquiryService: EnquiryFormService
  ) {}

  ngOnInit(): void {
    this.initForm();
    this.setupReferenceValidation();
  }

  initForm(): void {
    this.enquiryForm = this.fb.group({
      firstName: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(50)]],
      lastName: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(50)]],
      email: ['', [Validators.required, Validators.email]],
      mobile: ['', [Validators.required, Validators.pattern('^[0-9]{10}$')]],
      address: ['', [Validators.required, Validators.minLength(10), Validators.maxLength(500)]],
      age: ['', [Validators.required, Validators.min(18), Validators.max(100)]],
      incomeSource: ['', Validators.required],
      monthlyIncome: ['', [Validators.required, Validators.min(10000), Validators.max(10000000)]],
      budget: ['', [Validators.required, Validators.min(100000), Validators.max(100000000)]],
      reference: ['', Validators.required],
      referencePerson: [''],
      remarks: ['', [Validators.required, Validators.minLength(10), Validators.maxLength(1000)]],
      attendedBy: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(100)]],
      propertyInterests: this.fb.group({
        'studio-apt': [false],
        '1-bhk': [false],
        '2-bhk': [false],
        '3-bhk': [false],
        'jodi-flat': [false]
      }, { validators: this.atLeastOneSelected })
    });

    // Initialize property interests array to match form controls
    this.propertyInterests.forEach(interest => {
      interest.checked = false;
    });
  }

  setupReferenceValidation(): void {
    // Watch for changes in reference field
    this.enquiryForm.get('reference')?.valueChanges.subscribe(value => {
      const referencePersonControl = this.enquiryForm.get('referencePerson');
      
      if (value === 'friend' || value === 'agent') {
        referencePersonControl?.setValidators([Validators.required, Validators.minLength(2), Validators.maxLength(100)]);
      } else {
        referencePersonControl?.clearValidators();
        referencePersonControl?.setValue('');
      }
      
      referencePersonControl?.updateValueAndValidity();
    });
  }

  // Custom validator to ensure at least one property interest is selected
  atLeastOneSelected(group: FormGroup): {[key: string]: any} | null {
    const selected = Object.values(group.value).some(value => value === true);
    return selected ? null : { atLeastOneRequired: true };
  }

  // Getter methods for easy access to form controls
  get f() {
    return this.enquiryForm.controls;
  }

  get propertyInterestsGroup() {
    return this.enquiryForm.get('propertyInterests') as FormGroup;
  }

  // Check if reference person field should be shown
  shouldShowReferencePerson(): boolean {
    const referenceValue = this.enquiryForm.get('reference')?.value;
    return referenceValue === 'friend' || referenceValue === 'agent';
  }

  // Handle checkbox changes
  onInterestChange(event: any, interest: string): void {
    const checked = event.target.checked;
    this.propertyInterestsGroup.patchValue({ [interest]: checked });
    // Trigger validation update
    this.propertyInterestsGroup.updateValueAndValidity();
  }

  // Check if at least one property interest is selected
  isAtLeastOneInterestSelected(): boolean {
    const interests = this.propertyInterestsGroup.value;
    return Object.values(interests).some(value => value === true);
  }

  // Format number with commas for display
  formatNumber(value: string): string {
    if (!value) return '';
    const num = parseInt(value.replace(/,/g, ''), 10);
    return num.toLocaleString('en-IN');
  }

  // Handle input formatting for numbers
  onNumberInput(event: any, fieldName: string): void {
    const input = event.target;
    let value = input.value.replace(/[^\d]/g, '');
    
    if (value) {
      const num = parseInt(value, 10);
      input.value = num.toLocaleString('en-IN');
      // Update form control with numeric value
      this.enquiryForm.get(fieldName)?.setValue(num);
    } else {
      // Set empty string instead of null for validation
      this.enquiryForm.get(fieldName)?.setValue('');
    }
  }

  // Handle blur event for number inputs to ensure proper validation
  onNumberBlur(event: any, fieldName: string): void {
    const input = event.target;
    const value = input.value.replace(/[^\d]/g, '');
    
    if (value) {
      const num = parseInt(value, 10);
      input.value = num.toLocaleString('en-IN');
      this.enquiryForm.get(fieldName)?.setValue(num);
    } else {
      this.enquiryForm.get(fieldName)?.setValue('');
    }
  }

  // Map form data to API payload format
  private mapFormDataToApiPayload(): CustomerEnquiry {
    const formValue = this.enquiryForm.value;
    
    // Map property interests to the expected format with proper null handling
    const propertyInterestsMap: { [key: string]: boolean } = {};
    
    // Ensure propertyInterests exists and has proper values
    if (formValue.propertyInterests) {
      Object.keys(formValue.propertyInterests).forEach(key => {
        // Ensure boolean values - convert null/undefined to false
        propertyInterestsMap[key] = Boolean(formValue.propertyInterests[key]);
      });
    } else {
      // If propertyInterests is null/undefined, set all to false
      this.propertyInterests.forEach(interest => {
        propertyInterestsMap[interest.value] = false;
      });
    }

    // Create remarks array with the required format
    const remarksArray = [{
      remark: formValue.remarks,
      attendedBy: formValue.attendedBy,
      visitDate: new Date().toISOString()
    }];

    return {
      firstName: formValue.firstName,
      lastName: formValue.lastName,
      email: formValue.email,
      mobile: formValue.mobile,
      address: formValue.address,
      age: formValue.age,
      incomeSource: formValue.incomeSource,
      income: formValue.monthlyIncome, // Map monthlyIncome to income
      budget: formValue.budget,
      reference: formValue.reference,
      referencePerson: formValue.referencePerson || undefined,
      propertyInterests: propertyInterestsMap,
      remarks: remarksArray
    };
  }

  onSubmit(): void {
    this.submitted = true;

    // Log form validation status for debugging
    console.log('Form valid:', this.enquiryForm.valid);
    console.log('Form errors:', this.enquiryForm.errors);
    console.log('Form value:', this.enquiryForm.value);
    
    // Check individual field errors
    Object.keys(this.enquiryForm.controls).forEach(key => {
      const control = this.enquiryForm.get(key);
      if (control?.errors) {
        console.log(`${key} errors:`, control.errors);
      }
    });

    if (this.enquiryForm.valid) {
      this.isLoading = true;
      
      const customerData = this.mapFormDataToApiPayload();
      
      console.log('Submitting customer enquiry:', customerData);
      
      this.enquiryService.submitCustomerEnquiry(customerData)
        .pipe(
          catchError((error) => {
            console.error('Error submitting enquiry:', error);
        //    alert('Error submitting enquiry. Please try again.');
            return of(null);
          }),
          finalize(() => {
            this.isLoading = false;
          })
        )
        .subscribe((response) => {
          if (response) {
            console.log('Enquiry submitted successfully:', response);
         //   alert('Enquiry form submitted successfully!');
            this.resetForm();
          }
        });
    } else {
      console.log('Form has validation errors');
      // Show specific validation errors to user
      this.showValidationErrors();
    }
  }

  // Show specific validation errors to user
  private showValidationErrors(): void {
    const errors: string[] = [];
    
    Object.keys(this.enquiryForm.controls).forEach(key => {
      const control = this.enquiryForm.get(key);
      if (control?.errors) {
        if (control.errors['required']) {
          errors.push(`${key} is required`);
        } else if (control.errors['minlength']) {
          errors.push(`${key} must be at least ${control.errors['minlength'].requiredLength} characters`);
        } else if (control.errors['maxlength']) {
          errors.push(`${key} cannot exceed ${control.errors['maxlength'].requiredLength} characters`);
        } else if (control.errors['min']) {
          errors.push(`${key} must be at least ${control.errors['min'].min}`);
        } else if (control.errors['max']) {
          errors.push(`${key} cannot exceed ${control.errors['max'].max}`);
        }
      }
    });

    // Check property interests validation
    if (this.propertyInterestsGroup.errors?.['atLeastOneRequired']) {
      errors.push('Please select at least one property type');
    }

    if (errors.length > 0) {
     // alert('Please fix the following errors:\n' + errors.join('\n'));
    }
  }

  resetForm(): void {
    this.submitted = false;
    this.isLoading = false;
    this.enquiryForm.reset();
    
    // Reset property interests array
    this.propertyInterests.forEach(interest => {
      interest.checked = false;
    });
    
    // Ensure property interests form group is properly reset
    const propertyInterestsGroup = this.enquiryForm.get('propertyInterests') as FormGroup;
    if (propertyInterestsGroup) {
      propertyInterestsGroup.patchValue({
        'studio-apt': false,
        '1-bhk': false,
        '2-bhk': false,
        '3-bhk': false,
        'jodi-flat': false
      });
      propertyInterestsGroup.updateValueAndValidity();
    }
  }
}
<div class="enquiry-form-container">
  <div class="form-card">
    <div class="form-header">
      <h2>Property Enquiry Form</h2>
      <p>Please fill in your details to help us assist you better</p>
    </div>

    <form [formGroup]="enquiryForm" (ngSubmit)="onSubmit()" class="enquiry-form">
      
      <!-- Personal Information Section -->
      <div class="form-section">
        <h3>Personal Information</h3>
        
        <div class="form-row">
          <div class="form-group">
            <label for="firstName">First Name</label>
            <input 
              type="text" 
              id="firstName"
              formControlName="firstName"
              placeholder="Enter your first name"
              [class.error]="submitted && f['firstName'].errors"
            />
            <div class="error-message" *ngIf="submitted && f['firstName'].errors">
              <span *ngIf="f['firstName'].errors['required']">First name is required</span>
              <span *ngIf="f['firstName'].errors['minlength']">First name must be at least 2 characters</span>
              <span *ngIf="f['firstName'].errors['maxlength']">First name cannot exceed 50 characters</span>
            </div>
          </div>

          <div class="form-group">
            <label for="lastName">Last Name</label>
            <input 
              type="text" 
              id="lastName"
              formControlName="lastName"
              placeholder="Enter your last name"
              [class.error]="submitted && f['lastName'].errors"
            />
            <div class="error-message" *ngIf="submitted && f['lastName'].errors">
              <span *ngIf="f['lastName'].errors['required']">Last name is required</span>
              <span *ngIf="f['lastName'].errors['minlength']">Last name must be at least 2 characters</span>
              <span *ngIf="f['lastName'].errors['maxlength']">Last name cannot exceed 50 characters</span>
            </div>
          </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label for="email">Email</label>
            <input 
              type="email" 
              id="email"
              formControlName="email"
              placeholder="Enter your email address"
              [class.error]="submitted && f['email'].errors"
            />
            <div class="error-message" *ngIf="submitted && f['email'].errors">
              <span *ngIf="f['email'].errors['required']">Email is required</span>
              <span *ngIf="f['email'].errors['email']">Please enter a valid email address</span>
            </div>
          </div>

          <div class="form-group">
            <label for="mobile">Mobile Number</label>
            <input 
              type="tel" 
              id="mobile"
              formControlName="mobile"
              placeholder="Enter your 10-digit mobile number"
              maxlength="10"
              [class.error]="submitted && f['mobile'].errors"
            />
            <div class="error-message" *ngIf="submitted && f['mobile'].errors">
              <span *ngIf="f['mobile'].errors['required']">Mobile number is required</span>
              <span *ngIf="f['mobile'].errors['pattern']">Please enter a valid 10-digit mobile number</span>
            </div>
          </div>
        </div>

        <div class="form-group">
          <label for="address">Address</label>
          <textarea 
            id="address"
            formControlName="address"
            placeholder="Enter your complete address"
            rows="4"
            [class.error]="submitted && f['address'].errors"
          ></textarea>
          <div class="error-message" *ngIf="submitted && f['address'].errors">
            <span *ngIf="f['address'].errors['required']">Address is required</span>
            <span *ngIf="f['address'].errors['minlength']">Address must be at least 10 characters</span>
            <span *ngIf="f['address'].errors['maxlength']">Address cannot exceed 500 characters</span>
          </div>
        </div>

        <div class="form-group">
          <label for="age">Age</label>
          <input 
            type="number" 
            id="age"
            formControlName="age"
            placeholder="Enter your age"
            min="18"
            max="100"
            [class.error]="submitted && f['age'].errors"
          />
          <div class="error-message" *ngIf="submitted && f['age'].errors">
            <span *ngIf="f['age'].errors['required']">Age is required</span>
            <span *ngIf="f['age'].errors['min']">Age must be at least 18</span>
            <span *ngIf="f['age'].errors['max']">Age cannot exceed 100</span>
          </div>
        </div>
      </div>

      <!-- Financial Information Section -->
      <div class="form-section">
        <h3>Financial Information</h3>
        
        <div class="form-group">
          <label for="incomeSource">Income Source</label>
          <select 
            id="incomeSource"
            formControlName="incomeSource"
            [class.error]="submitted && f['incomeSource'].errors"
          >
            <option value="">Select your income source</option>
            <option *ngFor="let source of incomeSources" [value]="source.value">
              {{ source.label }}
            </option>
          </select>
          <div class="error-message" *ngIf="submitted && f['incomeSource'].errors">
            <span *ngIf="f['incomeSource'].errors['required']">Please select your income source</span>
          </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label for="monthlyIncome">Monthly Income (₹)</label>
            <input 
              type="text" 
              id="monthlyIncome"
              formControlName="monthlyIncome"
              placeholder="Enter monthly income"
              (input)="onNumberInput($event, 'monthlyIncome')"
              (blur)="onNumberBlur($event, 'monthlyIncome')"
              [class.error]="submitted && f['monthlyIncome'].errors"
            />
            <div class="error-message" *ngIf="submitted && f['monthlyIncome'].errors">
              <span *ngIf="f['monthlyIncome'].errors['required']">Monthly income is required</span>
              <span *ngIf="f['monthlyIncome'].errors['min']">Monthly income must be at least ₹10,000</span>
              <span *ngIf="f['monthlyIncome'].errors['max']">Monthly income cannot exceed ₹1,00,00,000</span>
            </div>
          </div>

          <div class="form-group">
            <label for="budget">Property Budget (₹)</label>
            <input 
              type="text" 
              id="budget"
              formControlName="budget"
              placeholder="Enter property budget"
              (input)="onNumberInput($event, 'budget')"
              (blur)="onNumberBlur($event, 'budget')"
              [class.error]="submitted && f['budget'].errors"
            />
            <div class="error-message" *ngIf="submitted && f['budget'].errors">
              <span *ngIf="f['budget'].errors['required']">Property budget is required</span>
              <span *ngIf="f['budget'].errors['min']">Property budget must be at least ₹1,00,000</span>
              <span *ngIf="f['budget'].errors['max']">Property budget cannot exceed ₹10,00,00,000</span>
            </div>
          </div>
        </div>
      </div>

      <!-- Reference Section -->
      <div class="form-section">
        <h3>How did you hear about us?</h3>
        
        <div class="form-group">
          <label>Reference</label>
          <div class="radio-group">
            <div class="radio-item" *ngFor="let option of referenceOptions">
              <input 
                type="radio" 
                [id]="option.value"
                [value]="option.value"
                formControlName="reference"
                name="reference"
              />
              <label [for]="option.value">{{ option.label }}</label>
            </div>
          </div>
          <div class="error-message" *ngIf="submitted && f['reference'].errors">
            <span *ngIf="f['reference'].errors['required']">Please select how you heard about us</span>
          </div>
        </div>

        <!-- Conditional Reference Person Field -->
        <div class="form-group" *ngIf="shouldShowReferencePerson()">
          <label for="referencePerson">Reference Person Name</label>
          <input 
            type="text" 
            id="referencePerson"
            formControlName="referencePerson"
            placeholder="Enter the person's name who referred you"
            [class.error]="submitted && f['referencePerson'].errors"
          />
          <div class="error-message" *ngIf="submitted && f['referencePerson'].errors">
            <span *ngIf="f['referencePerson'].errors['required']">Reference person name is required</span>
            <span *ngIf="f['referencePerson'].errors['minlength']">Name must be at least 2 characters</span>
            <span *ngIf="f['referencePerson'].errors['maxlength']">Name cannot exceed 100 characters</span>
          </div>
        </div>
      </div>

      <!-- Property Interest Section -->
      <div class="form-section">
        <h3>Property Preferences</h3>
        
        <div class="form-group">
          <label>Interested in</label>
          <div class="checkbox-group" formGroupName="propertyInterests">
            <div class="checkbox-item" *ngFor="let interest of propertyInterests">
              <input 
                type="checkbox" 
                [id]="interest.value"
                [value]="interest.value"
                (change)="onInterestChange($event, interest.value)"
                [checked]="propertyInterestsGroup.get(interest.value)?.value"
              />
              <label [for]="interest.value">{{ interest.label }}</label>
            </div>
          </div>
          <div class="error-message" *ngIf="submitted && propertyInterestsGroup.errors?.['atLeastOneRequired']">
            Please select at least one property type
          </div>
        </div>
      </div>

      <!-- Remarks and Attendance Section -->
      <div class="form-section">
        <h3>Additional Information</h3>
        
        <div class="form-group">
          <label for="remarks">Remarks</label>
          <textarea 
            id="remarks"
            formControlName="remarks"
            placeholder="Enter detailed remarks about the customer interaction"
            rows="4"
            [class.error]="submitted && f['remarks'].errors"
          ></textarea>
          <div class="error-message" *ngIf="submitted && f['remarks'].errors">
            <span *ngIf="f['remarks'].errors['required']">Remarks are required</span>
            <span *ngIf="f['remarks'].errors['minlength']">Remarks must be at least 10 characters</span>
            <span *ngIf="f['remarks'].errors['maxlength']">Remarks cannot exceed 1000 characters</span>
          </div>
        </div>

        <div class="form-group">
          <label for="attendedBy">Attended By</label>
          <input 
            type="text" 
            id="attendedBy"
            formControlName="attendedBy"
            placeholder="Enter the name of the person who attended"
            [class.error]="submitted && f['attendedBy'].errors"
          />
          <div class="error-message" *ngIf="submitted && f['attendedBy'].errors">
            <span *ngIf="f['attendedBy'].errors['required']">Attended by field is required</span>
            <span *ngIf="f['attendedBy'].errors['minlength']">Name must be at least 2 characters</span>
            <span *ngIf="f['attendedBy'].errors['maxlength']">Name cannot exceed 100 characters</span>
          </div>
        </div>
      </div>

      <!-- Submit Section -->
      <div class="form-actions">
        <button type="submit" class="btn-submit" [disabled]="submitted && enquiryForm.invalid || isLoading">
          <span *ngIf="!isLoading && (!submitted || enquiryForm.valid)">Submit Enquiry</span>
          <span *ngIf="!isLoading && submitted && enquiryForm.invalid">Please fix errors</span>
          <span *ngIf="isLoading">Submitting...</span>
        </button>
        
        <button type="button" class="btn-reset" (click)="resetForm()" [disabled]="isLoading">
          Reset Form
        </button>
      </div>

    </form>
  </div>
</div>
